import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";
import { fileURLToPath } from "url";

// Fix __dirname for ESM
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(__dirname, "client", "src"),
      "@shared": path.resolve(__dirname, "shared"),
      "@assets": path.resolve(__dirname, "attached_assets"),
    },
  },
  root: path.resolve(__dirname, "client"),
  build: {
    outDir: path.resolve(__dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000 and bind to 0.0.0.0 for Replit
  const port = 5000;
  server.listen(port, "0.0.0.0", () => {
    log(`serving on port ${port}`);
  });

})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertBlogPostSchema, insertBlogCategorySchema, insertBlogMediaSchema, contactFormSchema, insertLeadSchema, updateLeadSchema, insertAdminUserSchema } from "@shared/schema";
import bcrypt from "bcrypt";
import { getUncachableResendClient } from "./resend-client";

// Admin authentication middleware using environment variables
const adminAuth = (req: Request, res: Response, next: NextFunction) => {
  const authHeader = req.headers.authorization;

  // Check for Bearer token from environment
  if (authHeader && authHeader.startsWith('Bearer ')) {
    const token = authHeader.slice(7);
    const adminToken = process.env.ADMIN_TOKEN;
    if (adminToken && token === adminToken) {
      return next();
    }
  }

  // Check for session-based auth from environment
  const adminPassword = req.headers['x-admin-password'];
  const envAdminPassword = process.env.ADMIN_PASSWORD;
  if (envAdminPassword && adminPassword === envAdminPassword) {
    return next();
  }

  return res.status(401).json({ error: 'Unauthorized: Admin access required' });
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Blog Categories API
  app.get("/api/blog/categories", async (req, res) => {
    try {
      const categories = await storage.getAllCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch categories" });
    }
  });

  app.get("/api/blog/categories/:id", async (req, res) => {
    try {
      const category = await storage.getCategory(req.params.id);
      if (!category) {
        return res.status(404).json({ error: "Category not found" });
      }
      res.json(category);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch category" });
    }
  });

  app.post("/api/blog/categories", adminAuth, async (req, res) => {
    try {
      const result = insertBlogCategorySchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid category data", details: result.error.issues });
      }

      const category = await storage.createCategory(result.data);
      res.status(201).json(category);
    } catch (error) {
      res.status(500).json({ error: "Failed to create category" });
    }
  });

  // Blog Posts API
  app.get("/api/blog/posts", async (req, res) => {
    try {
      const { published, categoryId, featured } = req.query;
      const filters: any = {};

      if (published !== undefined) filters.published = published === 'true';
      if (categoryId) filters.categoryId = categoryId as string;
      if (featured !== undefined) filters.featured = featured === 'true';

      const posts = await storage.getAllPosts(filters);
      res.json(posts);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch posts" });
    }
  });

  app.get("/api/blog/posts/:slug", async (req, res) => {
    try {
      const post = await storage.getPostBySlug(req.params.slug);
      if (!post) {
        return res.status(404).json({ error: "Post not found" });
      }

      // Increment view count
      await storage.incrementViewCount(post.id);

      // Get associated media
      const media = await storage.getMediaByPostId(post.id);

      res.json({ ...post, media });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch post" });
    }
  });

  app.post("/api/blog/posts", adminAuth, async (req, res) => {
    try {
      const result = insertBlogPostSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid post data", details: result.error.issues });
      }

      // Generate slug from title if not provided
      if (!result.data.slug) {
        result.data.slug = result.data.title
          .toLowerCase()
          .replace(/[^a-z0-9\s-]/g, '')
          .replace(/\s+/g, '-')
          .trim();
      }

      // Check if slug already exists
      const existingPost = await storage.getPostBySlug(result.data.slug);
      if (existingPost) {
        result.data.slug = `${result.data.slug}-${Date.now()}`;
      }

      const post = await storage.createPost(result.data);
      res.status(201).json(post);
    } catch (error) {
      res.status(500).json({ error: "Failed to create post" });
    }
  });

  app.put("/api/blog/posts/:id", adminAuth, async (req, res) => {
    try {
      const result = insertBlogPostSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid post data", details: result.error.issues });
      }

      const post = await storage.updatePost(req.params.id, result.data);
      if (!post) {
        return res.status(404).json({ error: "Post not found" });
      }

      res.json(post);
    } catch (error) {
      res.status(500).json({ error: "Failed to update post" });
    }
  });

  app.delete("/api/blog/posts/:id", adminAuth, async (req, res) => {
    try {
      const success = await storage.deletePost(req.params.id);
      if (!success) {
        return res.status(404).json({ error: "Post not found" });
      }

      res.json({ message: "Post deleted successfully" });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete post" });
    }
  });

  // Blog Media API
  app.get("/api/blog/posts/:postId/media", async (req, res) => {
    try {
      const media = await storage.getMediaByPostId(req.params.postId);
      res.json(media);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch media" });
    }
  });

  app.post("/api/blog/media", adminAuth, async (req, res) => {
    try {
      const result = insertBlogMediaSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid media data", details: result.error.issues });
      }

      const media = await storage.createMedia(result.data);
      res.status(201).json(media);
    } catch (error) {
      res.status(500).json({ error: "Failed to create media" });
    }
  });

  app.delete("/api/blog/media/:id", adminAuth, async (req, res) => {
    try {
      const success = await storage.deleteMedia(req.params.id);
      if (!success) {
        return res.status(404).json({ error: "Media not found" });
      }

      res.json({ message: "Media deleted successfully" });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete media" });
    }
  });

  // Contact Form API
  app.post("/api/contact", async (req, res) => {
    try {
      const result = contactFormSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ 
          error: "Invalid form data", 
          details: result.error.issues 
        });
      }

      const { name, email, businessNeeds, message } = result.data;

      // Save lead to database
      await storage.createLead({
        name,
        email,
        businessNeeds,
        message: message || null,
        status: "new",
        source: "website"
      });

      // Get Resend client and send email
      const { client, fromEmail } = await getUncachableResendClient();

      // Send email to sales team
      const emailResponse = await client.emails.send({
        from: fromEmail || 'onboarding@resend.dev',
        to: 'sales@neuralcoderai.com',
        subject: `New Contact Inquiry from ${name}`,
        html: `
          <h2>New Contact Form Submission</h2>
          <p><strong>Name:</strong> ${name}</p>
          <p><strong>Email:</strong> ${email}</p>
          <p><strong>Business Needs:</strong> ${businessNeeds}</p>
          <p><strong>Message:</strong> ${message || 'No message provided'}</p>
          <hr>
          <p><small>This email was sent from the contact form on your website.</small></p>
        `,
      });

      res.status(200).json({ 
        success: true, 
        message: "Your message has been sent successfully! We'll get back to you soon." 
      });
    } catch (error: any) {
      console.error("Contact form error:", error);
      res.status(500).json({ 
        error: "Failed to send message. Please try again later.",
        details: error.message 
      });
    }
  });

  // Admin Authentication API
  app.post("/api/admin/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      const admin = await storage.getAdminUserByUsername(username);
      if (!admin) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const isValid = await bcrypt.compare(password, admin.password);
      if (!isValid) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      res.json({ 
        success: true, 
        admin: { id: admin.id, username: admin.username, role: admin.role }
      });
    } catch (error) {
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/admin/register", async (req, res) => {
    try {
      const result = insertAdminUserSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid admin data", details: result.error.issues });
      }

      const hashedPassword = await bcrypt.hash(result.data.password, 10);
      const admin = await storage.createAdminUser({
        ...result.data,
        password: hashedPassword
      });

      res.status(201).json({ 
        success: true, 
        admin: { id: admin.id, username: admin.username, role: admin.role }
      });
    } catch (error: any) {
      if (error.message?.includes('unique')) {
        return res.status(409).json({ error: "Username or email already exists" });
      }
      res.status(500).json({ error: "Failed to create admin" });
    }
  });

  // Leads Management API
  app.get("/api/admin/leads", adminAuth, async (req, res) => {
    try {
      const { status } = req.query;
      const filters: any = {};
      if (status) filters.status = status as string;

      const leads = await storage.getAllLeads(filters);
      res.json(leads);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch leads" });
    }
  });

  app.get("/api/admin/leads/stats", adminAuth, async (req, res) => {
    try {
      const stats = await storage.getLeadStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch lead stats" });
    }
  });

  app.get("/api/admin/leads/:id", adminAuth, async (req, res) => {
    try {
      const lead = await storage.getLead(req.params.id);
      if (!lead) {
        return res.status(404).json({ error: "Lead not found" });
      }
      res.json(lead);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch lead" });
    }
  });

  app.post("/api/admin/leads", adminAuth, async (req, res) => {
    try {
      const result = insertLeadSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid lead data", details: result.error.issues });
      }

      const lead = await storage.createLead(result.data);
      res.status(201).json(lead);
    } catch (error) {
      res.status(500).json({ error: "Failed to create lead" });
    }
  });

  app.patch("/api/admin/leads/:id", adminAuth, async (req, res) => {
    try {
      const result = updateLeadSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ error: "Invalid lead data", details: result.error.issues });
      }

      const lead = await storage.updateLead(req.params.id, result.data);
      if (!lead) {
        return res.status(404).json({ error: "Lead not found" });
      }

      res.json(lead);
    } catch (error) {
      res.status(500).json({ error: "Failed to update lead" });
    }
  });

  app.delete("/api/admin/leads/:id", adminAuth, async (req, res) => {
    try {
      const success = await storage.deleteLead(req.params.id);
      if (!success) {
        return res.status(404).json({ error: "Lead not found" });
      }

      res.json({ message: "Lead deleted successfully" });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete lead" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser,
  type BlogPost,
  type InsertBlogPost,
  type BlogCategory,
  type InsertBlogCategory,
  type BlogMedia,
  type InsertBlogMedia,
  type Lead,
  type InsertLead,
  type UpdateLead,
  type AdminUser,
  type InsertAdminUser,
  users,
  blogCategories,
  blogPosts,
  blogMedia,
  leads,
  adminUsers
} from "@shared/schema";
import { randomUUID } from "crypto";
import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { eq, and, desc, sql as drizzleSql } from "drizzle-orm";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Blog Category operations
  getAllCategories(): Promise<BlogCategory[]>;
  getCategory(id: string): Promise<BlogCategory | undefined>;
  getCategoryBySlug(slug: string): Promise<BlogCategory | undefined>;
  createCategory(category: InsertBlogCategory): Promise<BlogCategory>;
  updateCategory(id: string, category: Partial<InsertBlogCategory>): Promise<BlogCategory | undefined>;
  deleteCategory(id: string): Promise<boolean>;

  // Blog Post operations
  getAllPosts(filters?: { published?: boolean; categoryId?: string; featured?: boolean }): Promise<BlogPost[]>;
  getPost(id: string): Promise<BlogPost | undefined>;
  getPostBySlug(slug: string): Promise<BlogPost | undefined>;
  createPost(post: InsertBlogPost): Promise<BlogPost>;
  updatePost(id: string, post: Partial<InsertBlogPost>): Promise<BlogPost | undefined>;
  deletePost(id: string): Promise<boolean>;
  incrementViewCount(id: string): Promise<void>;

  // Blog Media operations
  getMediaByPostId(postId: string): Promise<BlogMedia[]>;
  createMedia(media: InsertBlogMedia): Promise<BlogMedia>;
  deleteMedia(id: string): Promise<boolean>;

  // Lead operations
  getAllLeads(filters?: { status?: string }): Promise<Lead[]>;
  getLead(id: string): Promise<Lead | undefined>;
  createLead(lead: InsertLead): Promise<Lead>;
  updateLead(id: string, lead: UpdateLead): Promise<Lead | undefined>;
  deleteLead(id: string): Promise<boolean>;
  getLeadStats(): Promise<{
    total: number;
    new: number;
    contacted: number;
    qualified: number;
    converted: number;
    closed: number;
    rejected: number;
  }>;

  // Admin User operations
  getAdminUser(id: string): Promise<AdminUser | undefined>;
  getAdminUserByUsername(username: string): Promise<AdminUser | undefined>;
  createAdminUser(admin: InsertAdminUser): Promise<AdminUser>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private blogCategories: Map<string, BlogCategory>;
  private blogPosts: Map<string, BlogPost>;
  private blogMedia: Map<string, BlogMedia>;
  private leads: Map<string, Lead>;
  private adminUsers: Map<string, AdminUser>;

  constructor() {
    this.users = new Map();
    this.blogCategories = new Map();
    this.blogPosts = new Map();
    this.blogMedia = new Map();
    this.leads = new Map();
    this.adminUsers = new Map();

    // Add some default categories
    this.initializeDefaultCategories();
  }

  private initializeDefaultCategories() {
    const defaultCategories: BlogCategory[] = [
      {
        id: randomUUID(),
        name: "Technology",
        slug: "technology",
        description: "Latest trends in technology and AI",
        color: "#3B82F6",
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        name: "Innovation",
        slug: "innovation",
        description: "Breakthrough innovations and case studies",
        color: "#8B5CF6",
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        name: "Industry Insights",
        slug: "industry-insights",
        description: "Deep dives into industry trends",
        color: "#10B981",
        createdAt: new Date(),
      }
    ];

    defaultCategories.forEach(category => {
      this.blogCategories.set(category.id, category);
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Blog Category methods
  async getAllCategories(): Promise<BlogCategory[]> {
    return Array.from(this.blogCategories.values());
  }

  async getCategory(id: string): Promise<BlogCategory | undefined> {
    return this.blogCategories.get(id);
  }

  async getCategoryBySlug(slug: string): Promise<BlogCategory | undefined> {
    return Array.from(this.blogCategories.values()).find(cat => cat.slug === slug);
  }

  async createCategory(insertCategory: InsertBlogCategory): Promise<BlogCategory> {
    const id = randomUUID();
    const category: BlogCategory = { 
      ...insertCategory, 
      id, 
      description: insertCategory.description || null,
      color: insertCategory.color || null,
      createdAt: new Date() 
    };
    this.blogCategories.set(id, category);
    return category;
  }

  async updateCategory(id: string, updateData: Partial<InsertBlogCategory>): Promise<BlogCategory | undefined> {
    const category = this.blogCategories.get(id);
    if (!category) return undefined;

    const updatedCategory = { ...category, ...updateData };
    this.blogCategories.set(id, updatedCategory);
    return updatedCategory;
  }

  async deleteCategory(id: string): Promise<boolean> {
    return this.blogCategories.delete(id);
  }

  // Blog Post methods
  async getAllPosts(filters?: { published?: boolean; categoryId?: string; featured?: boolean }): Promise<BlogPost[]> {
    let posts = Array.from(this.blogPosts.values());

    if (filters?.published !== undefined) {
      posts = posts.filter(post => post.published === filters.published);
    }
    if (filters?.categoryId) {
      posts = posts.filter(post => post.categoryId === filters.categoryId);
    }
    if (filters?.featured !== undefined) {
      posts = posts.filter(post => post.featuredPost === filters.featured);
    }

    return posts.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async getPost(id: string): Promise<BlogPost | undefined> {
    return this.blogPosts.get(id);
  }

  async getPostBySlug(slug: string): Promise<BlogPost | undefined> {
    return Array.from(this.blogPosts.values()).find(post => post.slug === slug);
  }

  async createPost(insertPost: InsertBlogPost): Promise<BlogPost> {
    const id = randomUUID();
    const now = new Date();
    const post: BlogPost = { 
      ...insertPost, 
      id, 
      viewCount: 0,
      tags: insertPost.tags || [],
      published: insertPost.published || false,
      featuredPost: insertPost.featuredPost || false,
      readTime: insertPost.readTime || 5,
      coverImage: insertPost.coverImage || null,
      categoryId: insertPost.categoryId || null,
      metaTitle: insertPost.metaTitle || null,
      metaDescription: insertPost.metaDescription || null,
      socialImage: insertPost.socialImage || null,
      createdAt: now,
      updatedAt: now,
      publishedAt: insertPost.published ? now : null
    };
    this.blogPosts.set(id, post);
    return post;
  }

  async updatePost(id: string, updateData: Partial<InsertBlogPost>): Promise<BlogPost | undefined> {
    const post = this.blogPosts.get(id);
    if (!post) return undefined;

    const updatedPost = { 
      ...post, 
      ...updateData, 
      updatedAt: new Date(),
      publishedAt: updateData.published && !post.published ? new Date() : post.publishedAt
    };
    this.blogPosts.set(id, updatedPost);
    return updatedPost;
  }

  async deletePost(id: string): Promise<boolean> {
    // Also delete associated media
    const mediaItems = Array.from(this.blogMedia.values()).filter(media => media.blogPostId === id);
    mediaItems.forEach(media => this.blogMedia.delete(media.id));

    return this.blogPosts.delete(id);
  }

  async incrementViewCount(id: string): Promise<void> {
    const post = this.blogPosts.get(id);
    if (post) {
      post.viewCount += 1;
      this.blogPosts.set(id, post);
    }
  }

  // Blog Media methods
  async getMediaByPostId(postId: string): Promise<BlogMedia[]> {
    return Array.from(this.blogMedia.values()).filter(media => media.blogPostId === postId);
  }

  async createMedia(insertMedia: InsertBlogMedia): Promise<BlogMedia> {
    const id = randomUUID();
    const media: BlogMedia = { 
      ...insertMedia, 
      id, 
      title: insertMedia.title || null,
      description: insertMedia.description || null,
      altText: insertMedia.altText || null,
      fileSize: insertMedia.fileSize || null,
      mimeType: insertMedia.mimeType || null,
      width: insertMedia.width || null,
      height: insertMedia.height || null,
      createdAt: new Date() 
    };
    this.blogMedia.set(id, media);
    return media;
  }

  async deleteMedia(id: string): Promise<boolean> {
    return this.blogMedia.delete(id);
  }

  // Lead methods
  async getAllLeads(filters?: { status?: string }): Promise<Lead[]> {
    let leadsArray = Array.from(this.leads.values());

    if (filters?.status) {
      leadsArray = leadsArray.filter(lead => lead.status === filters.status);
    }

    return leadsArray.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async getLead(id: string): Promise<Lead | undefined> {
    return this.leads.get(id);
  }

  async createLead(insertLead: InsertLead): Promise<Lead> {
    const id = randomUUID();
    const now = new Date();
    const lead: Lead = { 
      ...insertLead, 
      id, 
      message: insertLead.message || null,
      status: insertLead.status || "new",
      source: insertLead.source || "website",
      assignedTo: insertLead.assignedTo || null,
      notes: insertLead.notes || null,
      contactedAt: insertLead.contactedAt || null,
      closedAt: insertLead.closedAt || null,
      createdAt: now,
      updatedAt: now
    };
    this.leads.set(id, lead);
    return lead;
  }

  async updateLead(id: string, updateData: UpdateLead): Promise<Lead | undefined> {
    const lead = this.leads.get(id);
    if (!lead) return undefined;

    const updatedLead = { 
      ...lead, 
      ...updateData, 
      updatedAt: new Date()
    };
    this.leads.set(id, updatedLead);
    return updatedLead;
  }

  async deleteLead(id: string): Promise<boolean> {
    return this.leads.delete(id);
  }

  async getLeadStats(): Promise<{
    total: number;
    new: number;
    contacted: number;
    qualified: number;
    converted: number;
    closed: number;
    rejected: number;
  }> {
    const leadsArray = Array.from(this.leads.values());
    return {
      total: leadsArray.length,
      new: leadsArray.filter(l => l.status === "new").length,
      contacted: leadsArray.filter(l => l.status === "contacted").length,
      qualified: leadsArray.filter(l => l.status === "qualified").length,
      converted: leadsArray.filter(l => l.status === "converted").length,
      closed: leadsArray.filter(l => l.status === "closed").length,
      rejected: leadsArray.filter(l => l.status === "rejected").length,
    };
  }

  // Admin User methods
  async getAdminUser(id: string): Promise<AdminUser | undefined> {
    return this.adminUsers.get(id);
  }

  async getAdminUserByUsername(username: string): Promise<AdminUser | undefined> {
    return Array.from(this.adminUsers.values()).find(
      (admin) => admin.username === username,
    );
  }

  async createAdminUser(insertAdmin: InsertAdminUser): Promise<AdminUser> {
    const id = randomUUID();
    const admin: AdminUser = { 
      ...insertAdmin, 
      id, 
      role: insertAdmin.role || "admin",
      createdAt: new Date() 
    };
    this.adminUsers.set(id, admin);
    return admin;
  }
}

// PostgreSQL Database Storage Implementation
export class DatabaseStorage implements IStorage {
  private db;

  constructor() {
    if (!process.env.DATABASE_URL) {
      throw new Error("DATABASE_URL is not set");
    }
    const queryClient = neon(process.env.DATABASE_URL);
    this.db = drizzle(queryClient);

    // Initialize default categories
    this.initializeDefaultCategories();
  }

  private async initializeDefaultCategories() {
    try {
      const existingCategories = await this.db.select().from(blogCategories);
      if (existingCategories.length === 0) {
        const defaultCategories = [
          {
            name: "Technology",
            slug: "technology",
            description: "Latest trends in technology and AI",
            color: "#3B82F6",
          },
          {
            name: "Innovation",
            slug: "innovation",
            description: "Breakthrough innovations and case studies",
            color: "#8B5CF6",
          },
          {
            name: "Industry Insights",
            slug: "industry-insights",
            description: "Deep dives into industry trends",
            color: "#10B981",
          }
        ];

        for (const category of defaultCategories) {
          await this.db.insert(blogCategories).values(category);
        }
      }
    } catch (error) {
      console.error("Error initializing default categories:", error);
    }
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const result = await this.db.select().from(users).where(eq(users.id, id));
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await this.db.select().from(users).where(eq(users.username, username));
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await this.db.insert(users).values(insertUser).returning();
    return result[0];
  }

  // Blog Category operations
  async getAllCategories(): Promise<BlogCategory[]> {
    return await this.db.select().from(blogCategories);
  }

  async getCategory(id: string): Promise<BlogCategory | undefined> {
    const result = await this.db.select().from(blogCategories).where(eq(blogCategories.id, id));
    return result[0];
  }

  async getCategoryBySlug(slug: string): Promise<BlogCategory | undefined> {
    const result = await this.db.select().from(blogCategories).where(eq(blogCategories.slug, slug));
    return result[0];
  }

  async createCategory(category: InsertBlogCategory): Promise<BlogCategory> {
    const result = await this.db.insert(blogCategories).values(category).returning();
    return result[0];
  }

  async updateCategory(id: string, category: Partial<InsertBlogCategory>): Promise<BlogCategory | undefined> {
    const result = await this.db
      .update(blogCategories)
      .set(category)
      .where(eq(blogCategories.id, id))
      .returning();
    return result[0];
  }

  async deleteCategory(id: string): Promise<boolean> {
    const result = await this.db.delete(blogCategories).where(eq(blogCategories.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Blog Post operations
  async getAllPosts(filters?: { published?: boolean; categoryId?: string; featured?: boolean }): Promise<BlogPost[]> {
    let query = this.db.select().from(blogPosts);

    const conditions = [];
    if (filters?.published !== undefined) {
      conditions.push(eq(blogPosts.published, filters.published));
    }
    if (filters?.categoryId) {
      conditions.push(eq(blogPosts.categoryId, filters.categoryId));
    }
    if (filters?.featured !== undefined) {
      conditions.push(eq(blogPosts.featuredPost, filters.featured));
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions)) as any;
    }

    const result = await query.orderBy(desc(blogPosts.createdAt));
    return result;
  }

  async getPost(id: string): Promise<BlogPost | undefined> {
    const result = await this.db.select().from(blogPosts).where(eq(blogPosts.id, id));
    return result[0];
  }

  async getPostBySlug(slug: string): Promise<BlogPost | undefined> {
    const result = await this.db.select().from(blogPosts).where(eq(blogPosts.slug, slug));
    return result[0];
  }

  async createPost(post: InsertBlogPost): Promise<BlogPost> {
    const result = await this.db.insert(blogPosts).values({
      ...post,
      publishedAt: post.published ? new Date() : null
    }).returning();
    return result[0];
  }

  async updatePost(id: string, post: Partial<InsertBlogPost>): Promise<BlogPost | undefined> {
    const existing = await this.getPost(id);
    if (!existing) return undefined;

    const result = await this.db
      .update(blogPosts)
      .set({
        ...post,
        updatedAt: new Date(),
        publishedAt: post.published && !existing.published ? new Date() : existing.publishedAt
      })
      .where(eq(blogPosts.id, id))
      .returning();
    return result[0];
  }

  async deletePost(id: string): Promise<boolean> {
    // Delete associated media first
    await this.db.delete(blogMedia).where(eq(blogMedia.blogPostId, id));

    const result = await this.db.delete(blogPosts).where(eq(blogPosts.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async incrementViewCount(id: string): Promise<void> {
    await this.db
      .update(blogPosts)
      .set({ viewCount: drizzleSql`${blogPosts.viewCount} + 1` })
      .where(eq(blogPosts.id, id));
  }

  // Blog Media operations
  async getMediaByPostId(postId: string): Promise<BlogMedia[]> {
    return await this.db.select().from(blogMedia).where(eq(blogMedia.blogPostId, postId));
  }

  async createMedia(media: InsertBlogMedia): Promise<BlogMedia> {
    const result = await this.db.insert(blogMedia).values(media).returning();
    return result[0];
  }

  async deleteMedia(id: string): Promise<boolean> {
    const result = await this.db.delete(blogMedia).where(eq(blogMedia.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Lead operations
  async getAllLeads(filters?: { status?: string }): Promise<Lead[]> {
    let query = this.db.select().from(leads);

    if (filters?.status) {
      query = query.where(eq(leads.status, filters.status)) as any;
    }

    const result = await query.orderBy(desc(leads.createdAt));
    return result;
  }

  async getLead(id: string): Promise<Lead | undefined> {
    const result = await this.db.select().from(leads).where(eq(leads.id, id));
    return result[0];
  }

  async createLead(lead: InsertLead): Promise<Lead> {
    const result = await this.db.insert(leads).values(lead).returning();
    return result[0];
  }

  async updateLead(id: string, lead: UpdateLead): Promise<Lead | undefined> {
    const result = await this.db
      .update(leads)
      .set({ ...lead, updatedAt: new Date() })
      .where(eq(leads.id, id))
      .returning();
    return result[0];
  }

  async deleteLead(id: string): Promise<boolean> {
    const result = await this.db.delete(leads).where(eq(leads.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async getLeadStats(): Promise<{
    total: number;
    new: number;
    contacted: number;
    qualified: number;
    converted: number;
    closed: number;
    rejected: number;
  }> {
    const allLeads = await this.getAllLeads();
    return {
      total: allLeads.length,
      new: allLeads.filter(l => l.status === "new").length,
      contacted: allLeads.filter(l => l.status === "contacted").length,
      qualified: allLeads.filter(l => l.status === "qualified").length,
      converted: allLeads.filter(l => l.status === "converted").length,
      closed: allLeads.filter(l => l.status === "closed").length,
      rejected: allLeads.filter(l => l.status === "rejected").length,
    };
  }

  // Admin User operations
  async getAdminUser(id: string): Promise<AdminUser | undefined> {
    const result = await this.db.select().from(adminUsers).where(eq(adminUsers.id, id));
    return result[0];
  }

  async getAdminUserByUsername(username: string): Promise<AdminUser | undefined> {
    const result = await this.db.select().from(adminUsers).where(eq(adminUsers.username, username));
    return result[0];
  }

  async createAdminUser(admin: InsertAdminUser): Promise<AdminUser> {
    const result = await this.db.insert(adminUsers).values(admin).returning();
    return result[0];
  }
}

// Use DatabaseStorage if DATABASE_URL is available, otherwise use MemStorage
export const storage = process.env.DATABASE_URL ? new DatabaseStorage() : new MemStorage();

