}

client/src/components/Contact.tsx.content:
import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import { FaPhone, FaEnvelope, FaPaperPlane, FaMapMarkerAlt, FaCheckCircle } from "react-icons/fa";
import LogoComponent from "./LogoComponent";
import ind from "../image/india-flag-icon.svg";
import us from "../image/united-states-flag-icon.svg";
import uk from "../image/united-kingdom-flag-icon.svg";
import uae from "../image/united-arab-emirates-flag-icon.svg";
import ReactCountryFlag from "react-country-flag";

declare global {
  interface Window {
    Calendly?: any;
  }
}



const offices = [
  { 
    code: "IN",
    country: "INDIA", 
    location: "Bangalore", 
    address: "Unit, 101, Oxford Towers, 139, HAL Old Airport Rd, Kodihalli, Bengaluru, Karnataka 560008" 
  },
  { 
    code: "IN",
    country: "INDIA", 
    location: "Gurgaon", 
    address: "E-52, 1st Floor, Sushant Lok-3, Gurgaon, 122003"
  },
  { 
    code: "IN",
    country: "INDIA", 
    location: "Chandigarh", 
    address: "Sushma Infinium,Chandigarh-Delhi ,NH-22, Zirakpur" 
  },
  { 
    code: "GB",
    country: "UK", 
    location: "London", 
    address: "34-37 Liverpool St, London EC2M 7PP, United Kingdom" 
  },
  { 
    code: "AE",
    country: "UAE", 
    location: "Dubai", 
    address: "10th floor, Dubai World Trade Center Sheikh Zayed Road, P.O. Box 293816 Dubai, United Arab Emirates" 
  },
  { 
    code: "US",
    country: "USA", 
    location: "San Francisco", 
    address: "WeWork : 600 California St, San Francisco, CA 94108, United States" 
  }
];

const businessNeeds = [
  "AI-Powered Mobile App Development",
  "Custom CRM and Web Applications", 
  "AI Integration for Automation",
  "IoT Solutions",
  "Machine Learning & Analytics",
  "Computer Vision Implementation",
  "Robotics Automation",
  "Blockchain Solutions",
  "Other"
];

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    businessNeeds: "",
    message: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState<{type: 'success' | 'error', message: string} | null>(null);

  useEffect(() => {
    // Load Calendly script
    const script = document.createElement('script');
    script.src = 'https://assets.calendly.com/assets/external/widget.js';
    script.async = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      const response = await fetch('/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setSubmitStatus({ type: 'success', message: data.message });
        setFormData({ name: "", email: "", businessNeeds: "", message: "" });
      } else {
        setSubmitStatus({ type: 'error', message: data.error || 'Failed to send message' });
      }
    } catch (error) {
      setSubmitStatus({ type: 'error', message: 'Network error. Please try again.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const openCalendly = () => {
    if (window.Calendly) {
      window.Calendly.initPopupWidget({ url: 'https://calendly.com/neuralcoder-ai/mobile-app-development-discovery-call' });
    }
  };

  return (
    <section id="contact" className="py-20 relative">
      <div className="container mx-auto px-6">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-6xl font-bold mb-8">
            Ready to <span className="text-accent">Collaborate?</span>
          </h2>
          <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
            Let's build your next breakthrough together.
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-16">
          {/* Contact Form */}
          <motion.div
            className="glassmorphism-strong rounded-3xl p-8"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="flex items-center gap-3 mb-6">
              <LogoComponent size="sm" showText={false} animate={true} />
              <h3 className="text-2xl font-bold text-accent">Quick Inquiry Form</h3>
            </div>
            <form onSubmit={handleSubmit} className="space-y-6">
              <motion.div
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.2 }}
              >
                <label htmlFor="name" className="block text-sm font-semibold mb-2 text-foreground">
                  Name *
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-xl glassmorphism border-0 focus:ring-2 focus:ring-accent text-foreground placeholder-muted-foreground"
                  placeholder="Your full name"
                  required
                  data-testid="input-name"
                />
              </motion.div>

              <motion.div
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.2 }}
              >
                <label htmlFor="email" className="block text-sm font-semibold mb-2 text-foreground">
                  Email *
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-xl glassmorphism border-0 focus:ring-2 focus:ring-accent text-foreground placeholder-muted-foreground"
                  placeholder="your.email@company.com"
                  required
                  data-testid="input-email"
                />
              </motion.div>

              <motion.div
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.2 }}
              >
                <label htmlFor="businessNeeds" className="block text-sm font-semibold mb-2 text-foreground">
                  Business Needs *
                </label>
                <select
                  id="businessNeeds"
                  name="businessNeeds"
                  value={formData.businessNeeds}
                  onChange={handleChange}
                  className="w-full px-4 py-3 rounded-xl glassmorphism border-0 focus:ring-2 focus:ring-accent text-foreground"
                  required
                  data-testid="select-business-needs"
                >
                  <option value="">Select your business needs</option>
                  {businessNeeds.map((need) => (
                    <option key={need} value={need} className="bg-background text-foreground">
                      {need}
                    </option>
                  ))}
                </select>
              </motion.div>

              <motion.div
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.2 }}
              >
                <label htmlFor="message" className="block text-sm font-semibold mb-2 text-foreground">
                  Message
                </label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  rows={4}
                  className="w-full px-4 py-3 rounded-xl glassmorphism border-0 focus:ring-2 focus:ring-accent text-foreground placeholder-muted-foreground resize-none"
                  placeholder="Tell us about your project..."
                  data-testid="textarea-message"
                />
              </motion.div>

              {submitStatus && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className={`p-4 rounded-xl flex items-center gap-3 ${
                    submitStatus.type === 'success' 
                      ? 'bg-green-500/20 text-green-400' 
                      : 'bg-red-500/20 text-red-400'
                  }`}
                >
                  {submitStatus.type === 'success' && <FaCheckCircle />}
                  <p className="text-sm">{submitStatus.message}</p>
                </motion.div>
              )}

              <motion.button
                type="submit"
                disabled={isSubmitting}
                className="w-full bg-accent text-black font-bold py-4 px-8 rounded-xl hover:bg-accent/80 transition-all duration-300 flex items-center justify-center gap-3 hover-glow disabled:opacity-50 disabled:cursor-not-allowed"
                whileHover={{ scale: isSubmitting ? 1 : 1.02 }}
                whileTap={{ scale: isSubmitting ? 1 : 0.98 }}
                data-testid="button-send-inquiry"
              >
                <FaPaperPlane />
                {isSubmitting ? 'Sending...' : 'Send Inquiry'}
              </motion.button>
            </form>
          </motion.div>

          {/* Contact Information */}
          <motion.div
            className="space-y-8"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            {/* Global Offices */}
<div className="glassmorphism-strong rounded-3xl p-8">
      <h3 className="text-2xl font-bold mb-6 text-accent flex items-center gap-3">
        <FaMapMarkerAlt />
        Global Offices
      </h3>
      <div className="grid grid-cols-2 gap-4">
        {offices.map((office, index) => (
          <motion.div
            key={`${office.country}-${office.location}-${index}`}
            className="glassmorphism p-4 rounded-xl text-center hover-lift"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1, duration: 0.6 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.05 }}
          >
            <motion.div
              className="w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-3 bg-white shadow-lg"
              whileHover={{ rotate: 10, scale: 1.1 }}
              transition={{ type: "spring", stiffness: 200 }}
            >
              <ReactCountryFlag
                countryCode={office.code} // pass ISO 2-letter code
                svg
                style={{ width: "3em", height: "3em", borderRadius: "50%" }}
              />
            </motion.div>

            <h4 className="font-semibold text-accent text-sm">{office.country}</h4>
            <p className="text-xs text-muted-foreground">{office.location}</p>
          </motion.div>
        ))}
      </div>
    </div>


            {/* Direct Contact */}
            <div className="glassmorphism-strong rounded-3xl p-8">
              <h3 className="text-2xl font-bold mb-6 text-accent">Direct Contact</h3>
              <div className="space-y-4">
                <motion.div 
                  className="flex items-center gap-4 p-4 glassmorphism rounded-xl hover-lift"
                  whileHover={{ x: 5 }}
                >
                  <FaEnvelope className="text-primary text-xl" />
                  <div>
                    <p className="font-semibold text-foreground">Email</p>
                    <p className="text-muted-foreground">hello@neuralcoder.ai</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="flex items-center gap-4 p-4 glassmorphism rounded-xl hover-lift"
                  whileHover={{ x: 5 }}
                >
                  <FaPhone className="text-primary text-xl" />
                  <div>
                    <p className="font-semibold text-foreground">Phone</p>
                    <p className="text-muted-foreground">Schedule a call</p>
                  </div>
                </motion.div>
              </div>
            </div>

            {/* CTA Button with Calendly */}
            <motion.button 
              onClick={openCalendly}
              className="w-full border border-accent px-8 py-4 rounded-xl text-accent font-semibold hover:bg-accent hover:text-black transition-all duration-300"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              data-testid="button-book-intro-call"
            >
              Book Intro Call
            </motion.button>
            <link href="https://assets.calendly.com/assets/external/widget.css" rel="stylesheet" />
          </motion.div>
        </div>
      </div>
    </section>
  );
}

client/src/components/CreativeAssets.tsx.content:
import { motion } from "framer-motion";

// Floating Code Particles
export const FloatingCodeParticles = () => (
  <div className="absolute inset-0 overflow-hidden pointer-events-none">
    {["{}", "[]", "</>", "AI", "ML", "IoT", "API", "UI"].map((code, index) => (
      <motion.div
        key={index}
        className="absolute text-primary/20 font-mono text-sm"
        initial={{
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          opacity: 0.3
        }}
        animate={{
          x: Math.random() * window.innerWidth,
          y: Math.random() * window.innerHeight,
          opacity: [0.1, 0.4, 0.1]
        }}
        transition={{
          duration: 15 + Math.random() * 10,
          repeat: Infinity,
          ease: "linear"
        }}
      >
        {code}
      </motion.div>
    ))}
  </div>
);

// Professional Team Avatar
export const TeamAvatar = ({ name, role, image }: { name: string; role: string; image?: string }) => (
  <motion.div 
    className="glassmorphism-strong p-6 rounded-2xl text-center hover-lift group"
    whileHover={{ y: -10, rotateY: 5 }}
    transition={{ duration: 0.3 }}
  >
    <div className="w-24 h-24 mx-auto mb-4 relative">
      <div className="w-full h-full bg-gradient-to-br from-primary/30 to-primary/10 rounded-full flex items-center justify-center">
        {image ? (
         <img src={image} alt={name} className="w-full h-full rounded-full object-cover" />
        ) : (
          <div className="w-16 h-16 bg-gradient-to-br from-primary to-primary/70 rounded-full flex items-center justify-center">
            <span className="text-black font-bold text-xl">{name.charAt(0)}</span>
          </div>
        )}
      </div>
      <motion.div 
        className="absolute -inset-1 bg-gradient-to-r from-primary via-transparent to-primary rounded-full opacity-0 group-hover:opacity-30"
        animate={{ rotate: [0, 360] }}
        transition={{ duration: 3, repeat: Infinity }}
      />
    </div>
    <h4 className="font-semibold text-white mb-1">{name}</h4>
    <p className="text-primary text-sm font-medium">{role}</p>
  </motion.div>
);

// Creative Statistics Display
export const StatsDisplay = ({ stats }: { stats: { label: string; value: string; icon: string }[] }) => (
  <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
    {stats.map((stat, index) => (
      <motion.div
        key={stat.label}
        className="glassmorphism-strong p-6 rounded-xl text-center hover-lift"
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ delay: index * 0.1, duration: 0.6 }}
        viewport={{ once: true }}
      >
        <div className="text-3xl mb-2">{stat.icon}</div>
        <motion.div
          className="text-2xl font-bold text-primary mb-1"
          initial={{ scale: 0 }}
          whileInView={{ scale: 1 }}
          transition={{ delay: index * 0.1 + 0.3, duration: 0.5, type: "spring" }}
          viewport={{ once: true }}
        >
          {stat.value}
        </motion.div>
        <p className="text-white/70 text-sm">{stat.label}</p>
      </motion.div>
    ))}
  </div>
);

// Interactive Process Steps
export const ProcessSteps = ({ steps }: { steps: { title: string; description: string; icon: string }[] }) => (
  <div className="space-y-8">
    {steps.map((step, index) => (
      <motion.div
        key={step.title}
        className="flex items-start gap-6"
        initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
        whileInView={{ opacity: 1, x: 0 }}
        transition={{ delay: index * 0.2, duration: 0.6 }}
        viewport={{ once: true }}
      >
        <motion.div 
          className="flex-shrink-0 w-16 h-16 glassmorphism-strong rounded-2xl flex items-center justify-center text-2xl"
          whileHover={{ scale: 1.1, rotateY: 180 }}
          transition={{ duration: 0.5 }}
        >
          {step.icon}
        </motion.div>
        <div className="flex-1">
          <motion.div 
            className="flex items-center gap-4 mb-2"
            whileHover={{ x: 10 }}
          >
            <h3 className="text-xl font-semibold text-primary">{step.title}</h3>
            <div className="flex-1 h-px bg-gradient-to-r from-primary/50 to-transparent" />
          </motion.div>
          <p className="text-white/70 leading-relaxed">{step.description}</p>
        </div>
      </motion.div>
    ))}
  </div>
);

export const ClientLogoDisplay = ({ logos }: { logos: { name: string; logo: string }[] }) => {
  // helper: check if logo looks like an image path
  const isImage = (logo: string) => /\.(png|jpe?g|webp|svg)$/i.test(logo);

  return (
    <motion.div 
      className="glassmorphism-strong p-8 rounded-3xl"
      whileInView={{ opacity: 1, scale: 1 }}
      initial={{ opacity: 0, scale: 0.9 }}
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <div className="grid grid-cols-3 md:grid-cols-6 gap-8 items-center">
        {logos.map((client, index) => (
          <motion.div
            key={client.name}
            className="aspect-square flex items-center justify-center p-4 glassmorphism rounded-xl hover-lift group"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1, duration: 0.6 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.05, rotateY: 10 }}
          >
            <div className="w-full h-full bg-gradient-to-br from-white/10 to-white/5 rounded-lg flex items-center justify-center text-white/60 font-bold text-lg group-hover:text-primary transition-colors">
              {isImage(client.logo) ? (
                <img
                  src={client.logo}
                  alt={client.name}
                  className="max-h-12 max-w-[80%] object-contain"
                />
              ) : (
                client.logo
              )}
            </div>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

// Technology Stack Visualization
export const TechStackVisualization = ({
  technologies,
}: {
  technologies: { name: string; icon: React.ComponentType<any> | string; level: number }[];
}) => (
  <div className="space-y-6">
    {technologies.map((tech, index) => (
      <motion.div
        key={tech.name}
        className="glassmorphism-strong p-4 rounded-xl hover-lift"
        initial={{ opacity: 0, x: -50 }}
        whileInView={{ opacity: 1, x: 0 }}
        transition={{ delay: index * 0.1, duration: 0.6 }}
        viewport={{ once: true }}
      >
        <div className="flex items-center gap-4 mb-3">
          {typeof tech.icon === "string" ? (
            <img src={tech.icon} alt={tech.name} className="w-6 h-6 object-contain" />
          ) : (
            <tech.icon className="text-2xl text-primary" />
          )}
          <span className="font-semibold text-white">{tech.name}</span>
        </div>

        <div className="relative h-2 bg-black/30 rounded-full overflow-hidden">
          <motion.div
            className="absolute inset-y-0 left-0 bg-gradient-to-r from-primary to-primary/70 rounded-full"
            initial={{ width: 0 }}
            whileInView={{ width: `${tech.level}%` }}
            transition={{ delay: index * 0.1 + 0.5, duration: 0.8 }}
            viewport={{ once: true }}
          />
        </div>
        <div className="text-right text-sm text-primary mt-1">{tech.level}%</div>
      </motion.div>
    ))}
  </div>
);

// Interactive Feature Cards
export const FeatureCard = ({ 
  title, 
  description, 
  icon: IconComponent, 
  color = "from-primary/20 to-primary/10" 
}: { 
  title: string; 
  description: string; 
  icon: React.ComponentType<any>; 
  color?: string;
}) => (
  <motion.div
    className="relative glassmorphism-strong p-8 rounded-3xl overflow-hidden hover-lift group cursor-pointer"
    whileHover={{ 
      y: -10,
      transition: { duration: 0.3 }
    }}
    initial={{ opacity: 0, y: 30 }}
    whileInView={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.6 }}
    viewport={{ once: true }}
  >
    <div className={`absolute inset-0 bg-gradient-to-br ${color} opacity-30`} />
    <div className="relative z-10">
      <motion.div
        className="w-16 h-16 glassmorphism rounded-2xl flex items-center justify-center mb-6 text-2xl text-primary"
        whileHover={{ scale: 1.2, rotate: 15 }}
        transition={{ duration: 0.3 }}
      >
        <IconComponent />
      </motion.div>
      <h3 className="text-xl font-bold text-white mb-4">{title}</h3>
      <p className="text-white/70 leading-relaxed">{description}</p>
      <motion.div 
        className="mt-6 flex items-center text-primary font-semibold"
        whileHover={{ x: 10 }}
      >
        Learn More →
      </motion.div>
    </div>
  </motion.div>
);

// Testimonial Card
export const TestimonialCard = ({ 
  quote, 
  author, 
  position, 
  company,
  avatar 
}: {
  quote: string;
  author: string;
  position: string;
  company: string;
  avatar?: string;
}) => (
  <motion.div
    className="glassmorphism-strong p-8 rounded-3xl hover-lift"
    whileHover={{ scale: 1.02 }}
    initial={{ opacity: 0, y: 30 }}
    whileInView={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.6 }}
    viewport={{ once: true }}
  >
    <div className="text-4xl text-primary mb-4">"</div>
    <p className="text-white/80 leading-relaxed mb-6 italic">{quote}</p>
    <div className="flex items-center gap-4">
      <div className="w-12 h-12 bg-gradient-to-br from-primary to-primary/70 rounded-full flex items-center justify-center">
        {avatar ? (
          <img src={avatar} alt={author} className="w-full h-full rounded-full object-cover" />
        ) : (
          <span className="text-black font-bold">{author.charAt(0)}</span>
        )}
      </div>
      <div>
        <p className="font-semibold text-white">{author}</p>
        <p className="text-sm text-primary">{position} at {company}</p>
      </div>
    </div>
  </motion.div>
);

client/src/components/CustomIllustration.content:
import { motion } from "framer-motion";

// AI Brain Network Illustration
export const AIBrainNetwork = ({ size = 200 }: { size?: number }) => (
  <motion.svg 
    width={size} 
    height={size} 
    viewBox="0 0 200 200" 
    fill="none"
    animate={{ rotate: [0, 5, 0, -5, 0] }}
    transition={{ duration: 8, repeat: Infinity, ease: "easeInOut" }}
  >
    <defs>
      <linearGradient id="brainGradient" x1="0%" y1="0%" x2="100%" y2="100%">
        <stop offset="0%" stopColor="hsl(51, 100%, 70%)" />
        <stop offset="100%" stopColor="hsl(51, 100%, 50%)" />
      </linearGradient>
      <filter id="glow">
        <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
        <feMerge> 
          <feMergeNode in="coloredBlur"/>
