              const springValue = useSpring(0, { 
                stiffness: 100, 
                damping: 30,
                restDelta: 0.001
              });

              return (
                <motion.div
                  key={stat.label}
                  className="text-center"
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ 
                    opacity: 1, 
                    y: 0,
                    transition: { 
                      delay: index * 0.1,
                      duration: 0.6,
                      onComplete: () => {
                        if (stat.value.includes('+')) {
                          const numValue = parseInt(stat.value);
                          springValue.set(numValue);
                        }
                      }
                    }
                  }}
                  viewport={{ once: true }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="mb-2 flex justify-center text-#FFFFFF">
  <img src={stat.icon} alt={stat.label} className="h-20 w-20 object-contain animate-bounce" />
</div>

                  <div className="text-3xl font-bold text-primary mb-2">
                    {stat.value.includes('+') ? (
                      <motion.span>
                        {Math.round(springValue.get())}{stat.value.slice(-1)}
                      </motion.span>
                    ) : (
                      stat.value
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground">{stat.label}</p>
                </motion.div>
              );
            })}
          </div>
        </motion.div>
      </div>
    </div>
  );
}
