import { motion } from "framer-motion";
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { FaPlus, FaSave, FaEye, FaTrash, FaEdit } from "react-icons/fa";
import { insertBlogPostSchema, type InsertBlogPost, type BlogPost, type BlogCategory } from "@shared/schema";
import { z } from "zod";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

// Form schema for UI (separate from API schema)
const blogPostFormSchema = z.object({
  title: z.string().min(1, "Title is required"),
  slug: z.string().min(1, "Slug is required"),
  excerpt: z.string().min(1, "Excerpt is required"),
  content: z.string().min(1, "Content is required"),
  coverImage: z.string().optional(),
  categoryId: z.string().optional(),
  published: z.boolean().optional().default(false),
  featuredPost: z.boolean().optional().default(false),
  readTime: z.number().min(1).optional().default(5),
  tagsInput: z.string().optional(), // String input that we'll split
  metaTitle: z.string().optional(),
  metaDescription: z.string().optional(),
  socialImage: z.string().optional(),
});

type BlogPostFormData = z.infer<typeof blogPostFormSchema>;

export default function BlogAdmin() {
  const [isCreating, setIsCreating] = useState(false);
  const [editingPost, setEditingPost] = useState<BlogPost | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch posts and categories
  const { data: posts = [] } = useQuery<BlogPost[]>({
    queryKey: ['/api/blog/posts'],
  });

  const { data: categories = [] } = useQuery<BlogCategory[]>({
    queryKey: ['/api/blog/categories'],
  });

  // Form setup
  const form = useForm<BlogPostFormData>({
    resolver: zodResolver(blogPostFormSchema),
    defaultValues: {
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      coverImage: "",
      categoryId: "",
      published: false,
      featuredPost: false,
      readTime: 5,
      tagsInput: "",
      metaTitle: "",
      metaDescription: "",
      socialImage: "",
    },
  });

  // Mutations
const createPostMutation = useMutation({
  mutationFn: (data: InsertBlogPost) => {
    const token = localStorage.getItem("token"); // or wherever you store admin token after login

    return apiRequest("POST", "/api/blog/posts", data, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  },
  onSuccess: () => {
    queryClient.invalidateQueries({ queryKey: ["/api/blog/posts"] });
    form.reset();
    setIsCreating(false);
    toast({
      title: "Post created!",
      description: "Your blog post has been created successfully.",
    });
  },
  onError: (error: any) => {
    toast({
      title: "Error",
      description:
        error?.response?.data?.error || "Failed to create post. Please try again.",
      variant: "destructive",
    });
  },
});


  const updatePostMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertBlogPost> }) =>
      apiRequest('PUT', `/api/blog/posts/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      form.reset();
      setEditingPost(null);
      toast({
        title: "Post updated!",
        description: "Your blog post has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update post. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deletePostMutation = useMutation({
    mutationFn: (id: string) => apiRequest('DELETE', `/api/blog/posts/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog/posts'] });
      toast({
        title: "Post deleted!",
        description: "Your blog post has been deleted.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete post. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: BlogPostFormData) => {
    // Transform form data to API format
    const postData: InsertBlogPost = {
      title: data.title,
      slug: data.slug,
      excerpt: data.excerpt,
      content: data.content,
      authorId: "admin", // Default author for now
      tags: data.tagsInput ? data.tagsInput.split(',').map(tag => tag.trim()).filter(Boolean) : [],
      coverImage: data.coverImage || undefined,
      categoryId: data.categoryId || undefined,
      published: data.published || false,
      featuredPost: data.featuredPost || false,
      readTime: data.readTime || 5,
      metaTitle: data.metaTitle || undefined,
      metaDescription: data.metaDescription || undefined,
      socialImage: data.socialImage || undefined,
    };

    if (editingPost) {
      updatePostMutation.mutate({ id: editingPost.id, data: postData });
    } else {
      createPostMutation.mutate(postData);
    }
  };

  const startEditing = (post: BlogPost) => {
    setEditingPost(post);
    setIsCreating(true);
    form.reset({
      title: post.title,
      slug: post.slug,
      excerpt: post.excerpt,
      content: post.content,
      coverImage: post.coverImage || "",
      categoryId: post.categoryId || "",
      published: post.published,
      featuredPost: post.featuredPost,
      readTime: post.readTime,
      tagsInput: post.tags?.join(', ') || "",
      metaTitle: post.metaTitle || "",
      metaDescription: post.metaDescription || "",
      socialImage: post.socialImage || "",
    });
  };

  const cancelEditing = () => {
    setIsCreating(false);
    setEditingPost(null);
    form.reset();
  };

  return (
    <div className="container mx-auto px-6 py-20">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-foreground">Blog Administration</h1>
          {!isCreating && (
            <Button
              onClick={() => setIsCreating(true)}
              className="bg-primary text-black hover:bg-primary/80"
              data-testid="button-create-post"
            >
              <FaPlus className="mr-2" />
              Create New Post
            </Button>
          )}
        </div>

        {/* Create/Edit Form */}
        {isCreating && (
          <motion.div
            className="glassmorphism-strong rounded-3xl p-8 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-foreground">
                {editingPost ? "Edit Post" : "Create New Post"}
              </h2>
              <Button
                variant="outline"
                onClick={cancelEditing}
                data-testid="button-cancel-edit"
              >
                Cancel
              </Button>
            </div>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <div className="grid md:grid-cols-2 gap-6">
                  {/* Title */}
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Title</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Enter post title..."
                            {...field}
                            data-testid="input-post-title"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Slug */}
                  <FormField
                    control={form.control}
                    name="slug"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Slug</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="post-url-slug"
                            {...field}
                            data-testid="input-post-slug"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Excerpt */}
                <FormField
                  control={form.control}
                  name="excerpt"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Excerpt</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Brief description of the post..."
                          rows={3}
                          {...field}
                          data-testid="textarea-post-excerpt"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Content */}
                <FormField
                  control={form.control}
                  name="content"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Content</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Write your post content here..."
                          rows={12}
                          {...field}
                          data-testid="textarea-post-content"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid md:grid-cols-2 gap-6">
                  {/* Category */}
                  <FormField
                    control={form.control}
                    name="categoryId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-post-category">
                              <SelectValue placeholder="Select a category" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {categories.map((category) => (
                              <SelectItem key={category.id} value={category.id}>
                                {category.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Read Time */}
                  <FormField
                    control={form.control}
                    name="readTime"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Read Time (minutes)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            min="1"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value) || 5)}
                            data-testid="input-read-time"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Tags */}
                <FormField
                  control={form.control}
                  name="tagsInput"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Tags (comma-separated)</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="AI, Technology, Innovation"
                          {...field}
                          data-testid="input-post-tags"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Cover Image */}
                <FormField
                  control={form.control}
                  name="coverImage"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Cover Image URL</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="https://example.com/image.jpg"
                          {...field}
                          data-testid="input-cover-image"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Toggles */}
                <div className="flex gap-6">
                  <FormField
                    control={form.control}
                    name="published"
                    render={({ field }) => (
                      <FormItem className="flex items-center gap-2">
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                            data-testid="switch-published"
                          />
                        </FormControl>
                        <FormLabel>Published</FormLabel>
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="featuredPost"
                    render={({ field }) => (
                      <FormItem className="flex items-center gap-2">
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                            data-testid="switch-featured"
                          />
                        </FormControl>
                        <FormLabel>Featured Post</FormLabel>
                      </FormItem>
                    )}
                  />
                </div>

                {/* Submit Button */}
                <div className="flex justify-end">
                  <Button
                    type="submit"
                    disabled={createPostMutation.isPending || updatePostMutation.isPending}
                    className="bg-primary text-black hover:bg-primary/80"
                    data-testid="button-save-post"
                  >
                    <FaSave className="mr-2" />
                    {editingPost ? "Update Post" : "Create Post"}
                  </Button>
                </div>
              </form>
            </Form>
          </motion.div>
        )}

        {/* Posts List */}
        <div className="grid gap-6">
          {posts.map((post, index) => (
            <motion.div
              key={post.id}
              className="glassmorphism-strong rounded-xl p-6"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1, duration: 0.6 }}
              data-testid={`card-admin-post-${post.id}`}
            >
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="text-xl font-bold text-foreground">{post.title}</h3>
                    {post.published && (
                      <Badge variant="secondary" className="bg-green-500/20 text-green-400">
                        Published
                      </Badge>
                    )}
                    {post.featuredPost && (
                      <Badge variant="secondary" className="bg-yellow-500/20 text-yellow-400">
                        Featured
                      </Badge>
                    )}
                  </div>
                  <p className="text-muted-foreground mb-3">{post.excerpt}</p>
                  <div className="flex items-center gap-4 text-sm text-muted-foreground">
                    <span>Views: {post.viewCount}</span>
                    <span>Read time: {post.readTime} min</span>
                    {post.tags && post.tags.length > 0 && (
                      <span>Tags: {post.tags.join(", ")}</span>
                    )}
                  </div>
                </div>
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => startEditing(post)}
                    data-testid={`button-edit-${post.id}`}
                  >
                    <FaEdit />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => deletePostMutation.mutate(post.id)}
                    disabled={deletePostMutation.isPending}
                    data-testid={`button-delete-${post.id}`}
                  >
                    <FaTrash />
                  </Button>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

client/src/components/BlogAdminGate.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { FaLock, FaEye, FaEyeSlash } from "react-icons/fa";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import BlogAdmin from "./BlogAdmin";

const ADMIN_PASSWORD = "admin123";

export default function BlogAdminGate() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate API check delay
    await new Promise(resolve => setTimeout(resolve, 500));

    if (password === ADMIN_PASSWORD) {
      setIsAuthenticated(true);
      toast({
        title: "Access granted",
        description: "Welcome to the blog administration panel.",
      });
    } else {
      toast({
        title: "Access denied",
        description: "Incorrect password. Please try again.",
        variant: "destructive",
      });
    }
    setIsLoading(false);
  };

  if (isAuthenticated) {
    return <BlogAdmin />;
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-background">
      <motion.div
        className="w-full max-w-md"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <Card className="glassmorphism-strong">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-bold text-foreground flex items-center justify-center gap-2">
              <FaLock className="text-primary" />
              Admin Access
            </CardTitle>
            <p className="text-muted-foreground">
              Enter the admin password to access the blog management panel.
            </p>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="space-y-4">
              <div className="relative">
                <Input
                  type={showPassword ? "text" : "password"}
                  placeholder="Admin password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="pr-12"
                  data-testid="input-admin-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 top-1/2 -translate-y-1/2 h-auto p-1 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <FaEyeSlash className="text-muted-foreground" />
                  ) : (
                    <FaEye className="text-muted-foreground" />
                  )}
                </Button>
              </div>
              <Button
                type="submit"
                className="w-full bg-primary text-black hover:bg-primary/80"
                disabled={isLoading || !password.trim()}
                data-testid="button-admin-login"
              >
                {isLoading ? "Authenticating..." : "Access Admin Panel"}
              </Button>
            </form>

            {/* Demo hint */}
            <div className="mt-6 p-3 bg-muted/20 rounded-lg text-center">
              <p className="text-sm text-muted-foreground">
                <strong>Demo Password:</strong> admin123
              </p>
              <p className="text-xs text-muted-foreground mt-1">
                In production, implement proper authentication
              </p>
            </div>
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
}

client/src/components/BlogDetail.tsx.content:
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { useInView } from "react-intersection-observer";
import { FaClock, FaEye, FaTag, FaCalendarAlt, FaUser, FaArrowLeft, FaShare } from "react-icons/fa";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { SocialShareButtons } from "@/components/SocialShare";
import { useDocumentMeta } from "@/hooks/use-document-title";
import type { BlogPost, BlogCategory } from "@shared/schema";

export default function BlogDetail() {
  const [, params] = useRoute("/blog/:slug");
  const slug = params?.slug;

  const { ref: sectionRef, inView: sectionInView } = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // Fetch blog post
  const { data: postData, isLoading: postLoading, error } = useQuery<BlogPost & { media?: any[] }>({
    queryKey: ['/api/blog/posts', slug],
    enabled: !!slug,
  });

  // Fetch categories
  const { data: categories = [] } = useQuery<BlogCategory[]>({
    queryKey: ['/api/blog/categories'],
  });

  // Fetch all posts for related posts filtering
  const { data: allPosts = [] } = useQuery<BlogPost[]>({
    queryKey: ['/api/blog/posts'],
    enabled: !!postData?.categoryId,
  });

  // Filter related posts client-side
  const relatedPosts = allPosts.filter(post => 
    post.published && 
    post.categoryId === postData?.categoryId && 
    post.id !== postData?.id
  );

  const formatDate = (dateString: string | Date | null) => {
    if (!dateString) return "";
    const date = typeof dateString === 'string' ? new Date(dateString) : dateString;
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const currentUrl = typeof window !== 'undefined' ? window.location.href : '';

  // Set document meta for SEO
  useDocumentMeta({
    title: postData ? `${postData.title} | Neural Coder AI Blog` : 'Blog | Neural Coder AI',
    description: postData?.metaDescription || postData?.excerpt || undefined,
    image: postData?.socialImage || postData?.coverImage || undefined,
    url: currentUrl,
  });

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl mb-4">😔</div>
          <h1 className="text-3xl font-bold text-foreground mb-4">Post Not Found</h1>
          <p className="text-muted-foreground mb-8">The blog post you're looking for doesn't exist.</p>
          <Link href="/blog">
            <Button data-testid="button-back-to-blog">
              <FaArrowLeft className="mr-2" />
              Back to Blog
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  if (postLoading) {
    return (
      <div className="min-h-screen py-20">
        <div className="container mx-auto px-6 max-w-4xl">
          {/* Loading skeleton */}
          <div className="animate-pulse">
            <div className="h-8 bg-muted/20 rounded w-1/4 mb-6" />
            <div className="h-12 bg-muted/20 rounded w-3/4 mb-4" />
            <div className="h-6 bg-muted/20 rounded w-1/2 mb-8" />
            <div className="h-64 bg-muted/20 rounded-xl mb-8" />
            <div className="space-y-4">
              {Array.from({ length: 8 }).map((_, i) => (
                <div key={i} className="h-4 bg-muted/20 rounded" />
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!postData) return null;

  const categoryName = categories.find(c => c.id === postData.categoryId)?.name;
  const filteredRelatedPosts = relatedPosts.slice(0, 3);

  return (
    <motion.div
      className="min-h-screen py-20"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <div className="container mx-auto px-6 max-w-4xl">
        {/* Back Button */}
        <motion.div
          className="mb-8"
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Link href="/blog">
            <Button variant="ghost" className="text-muted-foreground hover:text-foreground" data-testid="button-back-to-blog">
              <FaArrowLeft className="mr-2" />
              Back to Blog
            </Button>
          </Link>
        </motion.div>

        {/* Article Header */}
        <motion.header
          className="mb-12"
          ref={sectionRef}
          initial={{ opacity: 0, y: 30 }}
          animate={sectionInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          {/* Category Badge */}
          {categoryName && (
            <motion.div
              className="mb-4"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2, duration: 0.6 }}
            >
              <Badge variant="secondary" className="bg-primary/10 text-primary">
                {categoryName}
              </Badge>
            </motion.div>
          )}

          {/* Title */}
          <motion.h1
            className="text-4xl md:text-5xl font-bold text-foreground mb-6 leading-tight"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 0.6 }}
            data-testid="text-post-title"
          >
            {postData.title}
          </motion.h1>

          {/* Excerpt */}
          <motion.p
            className="text-xl text-muted-foreground mb-8 leading-relaxed"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4, duration: 0.6 }}
          >
            {postData.excerpt}
          </motion.p>

          {/* Meta Information */}
          <motion.div
            className="flex flex-wrap items-center gap-6 text-sm text-muted-foreground mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5, duration: 0.6 }}
          >
            <div className="flex items-center gap-2">
              <FaCalendarAlt />
              <span>{formatDate(postData.publishedAt)}</span>
            </div>
            <div className="flex items-center gap-2">
              <FaClock />
              <span>{postData.readTime} min read</span>
            </div>
            <div className="flex items-center gap-2">
              <FaEye />
              <span>{postData.viewCount} views</span>
            </div>
          </motion.div>

          {/* Tags */}
          {postData.tags && postData.tags.length > 0 && (
            <motion.div
              className="flex flex-wrap gap-2 mb-8"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6, duration: 0.6 }}
            >
              {postData.tags.map((tag, index) => (
                <motion.span
                  key={index}
                  className="inline-flex items-center gap-1 text-sm bg-muted/20 text-muted-foreground px-3 py-1 rounded-full"
                  whileHover={{ scale: 1.05 }}
                  data-testid={`tag-${tag}`}
                >
                  <FaTag className="text-xs" />
                  {tag}
                </motion.span>
              ))}
            </motion.div>
          )}

          {/* Social Share */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.7, duration: 0.6 }}
          >
            <SocialShareButtons
              url={currentUrl}
              title={postData.title}
              description={postData.excerpt}
            />
          </motion.div>
        </motion.header>

        {/* Cover Image */}
        {postData.coverImage && (
          <motion.div
            className="mb-12"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.3, duration: 0.8 }}
          >
            <div className="rounded-3xl overflow-hidden glassmorphism">
              <img
                src={postData.coverImage}
                alt={postData.title}
                className="w-full h-auto"
                data-testid="img-cover-image"
              />
            </div>
          </motion.div>
        )}

        {/* Article Content */}
        <motion.article
          className="glassmorphism-strong rounded-3xl p-8 md:p-12 mb-12"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.8 }}
        >
          <div 
            className="prose prose-lg max-w-none text-foreground prose-headings:text-foreground prose-strong:text-foreground prose-a:text-primary hover:prose-a:text-primary/80"
            dangerouslySetInnerHTML={{ __html: postData.content }}
            data-testid="content-post-body"
          />
        </motion.article>

        {/* Social Share Footer */}
        <motion.div
          className="glassmorphism-strong rounded-3xl p-6 mb-12"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5, duration: 0.6 }}
        >
          <div className="text-center">
            <h3 className="text-lg font-semibold text-foreground mb-4 flex items-center justify-center gap-2">
              <FaShare />
              Share this post
            </h3>
            <SocialShareButtons
              url={currentUrl}
              title={postData.title}
              description={postData.excerpt}
              size="lg"
            />
          </div>
        </motion.div>

        {/* Related Posts */}
        {filteredRelatedPosts.length > 0 && (
          <motion.section
            className="mb-12"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6, duration: 0.8 }}
          >
            <h2 className="text-2xl font-bold text-foreground mb-8">Related Posts</h2>
            <div className="grid md:grid-cols-3 gap-6">
              {filteredRelatedPosts.map((relatedPost, index) => (
                <motion.div
                  key={relatedPost.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 * index, duration: 0.6 }}
                  data-testid={`card-related-post-${relatedPost.id}`}
                >
                  <Link href={`/blog/${relatedPost.slug}`}>
                    <motion.div
                      className="glassmorphism rounded-xl p-4 cursor-pointer h-full"
                      whileHover={{ scale: 1.02, y: -2 }}
                      transition={{ duration: 0.2 }}
                    >
                      {relatedPost.coverImage && (
                        <img
                          src={relatedPost.coverImage}
                          alt={relatedPost.title}
                          className="w-full h-32 object-cover rounded-lg mb-3"
                        />
                      )}
                      <h3 className="font-semibold text-foreground mb-2 line-clamp-2">
                        {relatedPost.title}
                      </h3>
                      <p className="text-sm text-muted-foreground line-clamp-2">
                        {relatedPost.excerpt}
                      </p>
                      <div className="flex items-center gap-2 mt-3 text-xs text-muted-foreground">
                        <FaClock />
                        <span>{relatedPost.readTime} min read</span>
                      </div>
                    </motion.div>
                  </Link>
                </motion.div>
              ))}
            </div>
          </motion.section>
        )}
      </div>
    </motion.div>
  );
}

client/src/components/BlogListing.tsx.content:
import { motion } from "framer-motion";
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { Link } from "wouter";
import { useInView } from "react-intersection-observer";
import { FaClock, FaEye, FaTag, FaCalendarAlt, FaUser } from "react-icons/fa";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import type { BlogPost, BlogCategory } from "@shared/schema";

interface BlogListingProps {
  featured?: boolean;
  limit?: number;
  showFilters?: boolean;
}

export default function BlogListing({ featured = false, limit, showFilters = true }: BlogListingProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");

  const { ref: sectionRef, inView: sectionInView } = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // Fetch blog posts
  const { data: allPosts = [], isLoading: postsLoading } = useQuery<BlogPost[]>({
    queryKey: ['/api/blog/posts'],
    enabled: true,
  });

  // Filter posts client-side
  const posts = allPosts.filter(post => {
    if (!post.published) return false;
    if (featured && !post.featuredPost) return false;
    return true;
  });

  // Fetch categories
  const { data: categories = [] } = useQuery<BlogCategory[]>({
    queryKey: ['/api/blog/categories'],
    enabled: showFilters,
  });

  // Filter posts
  const filteredPosts = posts
    .filter(post => {
      const matchesSearch = post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          post.excerpt.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          post.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesCategory = selectedCategory === "all" || post.categoryId === selectedCategory;

      return matchesSearch && matchesCategory;
    })
    .slice(0, limit);

  const formatDate = (dateString: string | Date | null) => {
    if (!dateString) return "";
    const date = typeof dateString === 'string' ? new Date(dateString) : dateString;
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  if (postsLoading) {
    return (
      <div className="container mx-auto px-6 py-12">
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {Array.from({ length: 6 }).map((_, i) => (
            <div key={i} className="glassmorphism-strong rounded-3xl p-6 animate-pulse">
              <div className="h-48 bg-muted/20 rounded-xl mb-4" />
              <div className="h-4 bg-muted/20 rounded mb-2" />
              <div className="h-4 bg-muted/20 rounded w-3/4 mb-4" />
              <div className="h-20 bg-muted/20 rounded" />
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <section ref={sectionRef} className="py-20 relative overflow-hidden">
      <div className="container mx-auto px-6">
        {/* Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={sectionInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="text-4xl md:text-5xl font-bold mb-6 text-foreground"
            whileHover={{ scale: 1.02 }}
          >
            {featured ? "Featured " : "Latest "}
            <span className="text-primary">Blog Posts</span>
          </motion.h2>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto leading-relaxed">
            Insights, innovations, and industry expertise from our team of AI specialists.
          </p>
        </motion.div>

        {/* Filters */}
        {showFilters && (
          <motion.div
            className="glassmorphism-strong p-6 rounded-3xl mb-12"
            initial={{ opacity: 0, y: 20 }}
            animate={sectionInView ? { opacity: 1, y: 0 } : {}}
            transition={{ delay: 0.2, duration: 0.6 }}
          >
            <div className="flex flex-col md:flex-row gap-4">
              <div className="flex-1">
                <Input
                  placeholder="Search posts..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="bg-background/50 border-primary/20"
                  data-testid="input-search-posts"
                />
              </div>
              <div className="md:w-64">
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger className="bg-background/50 border-primary/20" data-testid="select-category">
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {categories.map((category) => (
                      <SelectItem key={category.id} value={category.id}>
                        {category.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </motion.div>
        )}

        {/* Blog Posts Grid */}
        {filteredPosts.length === 0 ? (
          <motion.div
            className="text-center py-20"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="text-6xl mb-4">📝</div>
            <h3 className="text-2xl font-bold text-foreground mb-4">No posts found</h3>
            <p className="text-muted-foreground">
              {searchTerm || selectedCategory !== "all" 
                ? "Try adjusting your search or filter criteria." 
                : "Check back soon for new content!"}
            </p>
          </motion.div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredPosts.map((post, index) => (
              <motion.div
                key={post.id}
                custom={index}
                variants={cardVariants}
                initial="hidden"
                animate="visible"
                className="group"
                data-testid={`card-blog-post-${post.id}`}
              >
                <Link href={`/blog/${post.slug}`}>
                  <motion.div
                    className="glassmorphism-strong rounded-3xl overflow-hidden cursor-pointer h-full flex flex-col"
                    whileHover={{ 
                      scale: 1.02,
                      y: -5,
                      transition: { duration: 0.3 }
                    }}
                    whileTap={{ scale: 0.98 }}
                  >
                    {/* Cover Image */}
                    {post.coverImage && (
                      <div className="h-48 overflow-hidden">
                        <img
                          src={post.coverImage}
                          alt={post.title}
                          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                          data-testid={`img-cover-${post.id}`}
                        />
                      </div>
                    )}

                    <div className="p-6 flex-1 flex flex-col">
                      {/* Category Badge */}
                      {post.categoryId && (
                        <div className="mb-3">
                          <Badge variant="secondary" className="bg-primary/10 text-primary">
                            {categories.find(c => c.id === post.categoryId)?.name || "Uncategorized"}
                          </Badge>
                        </div>
                      )}

                      {/* Title */}
                      <h3 className="text-xl font-bold text-foreground mb-3 group-hover:text-primary transition-colors line-clamp-2">
                        {post.title}
                      </h3>

                      {/* Excerpt */}
                      <p className="text-muted-foreground mb-4 flex-1 line-clamp-3">
                        {post.excerpt}
                      </p>

                      {/* Tags */}
                      {post.tags && post.tags.length > 0 && (
                        <div className="flex flex-wrap gap-2 mb-4">
                          {post.tags.slice(0, 3).map((tag, tagIndex) => (
                            <motion.span
                              key={tagIndex}
                              className="inline-flex items-center gap-1 text-xs bg-muted/20 text-muted-foreground px-2 py-1 rounded-full"
                              whileHover={{ scale: 1.05 }}
                            >
                              <FaTag className="text-xs" />
                              {tag}
                            </motion.span>
                          ))}
                          {post.tags.length > 3 && (
                            <span className="text-xs text-muted-foreground">
                              +{post.tags.length - 3} more
                            </span>
                          )}
                        </div>
                      )}

                      {/* Meta Information */}
                      <div className="flex items-center justify-between text-sm text-muted-foreground pt-4 border-t border-muted/20">
                        <div className="flex items-center gap-4">
                          <div className="flex items-center gap-1">
                            <FaClock className="text-xs" />
                            <span>{post.readTime} min read</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <FaEye className="text-xs" />
                            <span>{post.viewCount}</span>
                          </div>
                        </div>
                        <div className="flex items-center gap-1">
                          <FaCalendarAlt className="text-xs" />
                          <span>{formatDate(post.publishedAt)}</span>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                </Link>
              </motion.div>
            ))}
          </div>
        )}

        {/* Load More Button (if limited) */}
        {limit && filteredPosts.length >= limit && (
          <motion.div
            className="text-center mt-12"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <Link href="/blog">
              <motion.button
                className="px-8 py-4 bg-primary text-black rounded-xl font-semibold hover:bg-primary/80 transition-colors"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                data-testid="button-view-all-posts"
              >
                View All Posts →
              </motion.button>
            </Link>
          </motion.div>
        )}
      </div>
    </section>
  );
}

client/src/components/CountryFlags.tsx.content:
import { motion } from "framer-motion";
import ReactCountryFlag from "react-country-flag";

// Country flags and data for global reach
export const countriesData = [
  { flag: "🇺🇸", name: "United States", projects: 15, code: "US" },
  { flag: "🇮🇳", name: "India", projects: 12, code: "IN" },
  { flag: "🇬🇧", name: "United Kingdom", projects: 8, code: "GB" },
  { flag: "🇨🇦", name: "Canada", projects: 6, code: "CA" },
  { flag: "🇦🇺", name: "Australia", projects: 5, code: "AU" },
  { flag: "🇩🇪", name: "Germany", projects: 7, code: "DE" },
  { flag: "🇯🇵", name: "Japan", projects: 4, code: "JP" },
  { flag: "🇸🇬", name: "Singapore", projects: 3, code: "SG" },
  { flag: "🇰🇷", name: "South Korea", projects: 2, code: "KR" },
  { flag: "🇫🇷", name: "France", projects: 3, code: "FR" },
  { flag: "🇦🇪", name: "United Arab Emirates", projects: 4, code: "AE" }, // UAE
  { flag: "🇶🇦", name: "Qatar", projects: 2, code: "QA" }, // Qatar

];

export function AnimatedCountryFlags() {
  return (
    <div className="flex flex-wrap gap-4 justify-center">
      {countriesData.map((country, index) => (
        <motion.div
          key={country.code}
          className="flex flex-col items-center glassmorphism p-4 rounded-xl hover-lift"
          initial={{ opacity: 0, scale: 0.8 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ delay: index * 0.1, duration: 0.5 }}
          viewport={{ once: true }}
          whileHover={{ 
            scale: 1.1,
            y: -5,
            boxShadow: "0 10px 25px rgba(91, 206, 213, 0.2)"
          }}
        >
          <motion.div 
            className="text-4xl mb-2"
            animate={{ 
              rotate: [0, 5, -5, 0],
              scale: [1, 1.1, 1]
            }}
            transition={{ 
              duration: 3,
              repeat: Infinity,
              delay: index * 0.5
            }}
          >
           <ReactCountryFlag
                countryCode={country.code} // pass ISO 2-letter code
                svg
                style={{ width: "3em", height: "3em", borderRadius: "50%" }}
              />
          </motion.div>
          <div className="text-center">
            <div className="text-sm font-semibold text-foreground">{country.name}</div>
            <div className="text-xs text-primary">{country.projects} projects</div>
          </div>
        </motion.div>
      ))}
    </div>
  );
}

export function FloatingCountryBadges() {
  const topCountries = countriesData.slice(0, 5);

  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {topCountries.map((country, index) => (
        <motion.div
          key={country.code}
          className="absolute"
          initial={{ 
            x: Math.random() * window.innerWidth,
            y: Math.random() * window.innerHeight,
            opacity: 0
          }}
          animate={{
            x: Math.random() * (window.innerWidth - 100),
            y: Math.random() * (window.innerHeight - 100),
            opacity: [0, 0.6, 0],
            scale: [1, 1.2, 1]
          }}
          transition={{
            duration: 8 + index * 2,
            repeat: Infinity,
            delay: index * 1.5
          }}
        >
          <div className="glassmorphism p-2 rounded-lg text-center">
            <div className="text-xl">{country.flag}</div>
            <div className="text-xs text-primary font-semibold mt-1">
              {country.projects}+
            </div>
          </div>
        </motion.div>
      ))}
    </div>
  );
}

