              scale: [1, 1.02, 1],
              textShadow: [
                "0 0 10px rgba(0, 71, 171, 0.3)",
                "0 0 20px rgba(0, 71, 171, 0.6)",
                "0 0 10px rgba(0, 71, 171, 0.3)"
